mapper.select:
select rno, ano, fk_empno, ap_type, title, content, final_signyn, ap_systemFileName, writeday, modifyday ,nvl(feedbackcnt,0) as feedbackcnt, bookmark, name_kr, DEPARTMENT_NAME, TEAM_NAME, position from ( select rno, ano, fk_empno, ap_type, title, content, final_signyn, ap_systemFileName, writeday, modifyday , feedbackcnt, bookmark from ( select row_number() over (order by ano desc) as rno, ano, fk_empno, bookmark , ap_type, title, content, decode(final_signyn,0,'진행',1,'승인',2,'반려',3,'취소') as final_signyn , nvl2(ap_systemFileName,1,0) as ap_systemFileName , to_char(writeday,'yyyy-mm-dd hh24:mi') as writeday, nvl(to_char(modifyday, 'yyyy-mm-dd hh24:mi'),0) as modifyday from tbl_approval where status = 1 and fk_empno = #{employee_no}
and final_signyn = '0'
and final_signyn != '0'
and to_char(writeday,'yyyy-mm-dd') between #{searchStartday} and #{searchEndday}
and ap_type = #{ap_type}
and bookmark = 1
and lower(${searchType}) like '%'||lower('${searchWord}')||'%'
and ano in (select distinct fk_ano from tbl_approval_sign where lower(${searchType}) like '%'||lower('${searchWord}')||'%')
and ano in (select distinct fk_ano from tbl_approval_refer where lower(${searchType}) like '%'||lower('${searchWord}')||'%')
order by rno desc ) V left join ( select count(feedback) as feedbackcnt, fk_ano from tbl_approval_sign group by fk_ano ) S on v.ano = s.fk_ano where rno between #{startRno} and #{endRno} order by rno asc )A left join v_employee E on A.fk_empno = E.employee_no

with v as( select fk_ano, signstep, fk_sign_empno,signyn, feedback from tbl_approval_sign where fk_ano in (select fk_ano from tbl_approval_sign where fk_sign_empno = #{employee_no} and signyn=0) ) select rno, ano, fk_empno, ap_type, title, content, decode(final_signyn,0,'진행',1,'승인',2,'반려',3,'취소') as final_signyn, ap_systemFileName, writeday, modifyday ,nvl(feedbackcnt,0) as feedbackcnt, bookmark, name_kr, DEPARTMENT_NAME, TEAM_NAME, position from ( select row_number() over (order by ano desc) as rno, ano, fk_empno, ap_type, title, content, final_signyn, ap_systemFileName, writeday, modifyday ,nvl(feedbackcnt,0) as feedbackcnt, bookmark, name_kr, DEPARTMENT_NAME, TEAM_NAME, position from ( select ano, fk_empno, bookmark , ap_type, title, content, final_signyn , nvl2(ap_systemFileName,1,0) as ap_systemFileName , to_char(writeday,'yyyy-mm-dd hh24:mi') as writeday, nvl(to_char(modifyday, 'yyyy-mm-dd hh24:mi'),0) as modifyday from tbl_approval where status = 1 and final_signyn = 0
and ap_type = #{ap_type}
and bookmark = 1
) V right join ( select distinct fk_ano, count(feedback) as feedbackcnt from v where signyn != 0 or (fk_sign_empno = #{employee_no} and signstep=1 and signyn=0) start with fk_sign_empno = #{employee_no} connect by prior signstep-1 = signstep group by fk_ano )S on v.ano = s.fk_ano left join v_employee E on V.fk_empno = E.employee_no where final_signyn = 0 order by rno asc ) where rno between #{startRno} and #{endRno} order by rno asc

select dayoff_cnt
from TBL_EMPLOYEE
where employee_no = #{employee_no}

select distinct ${searchType}
select ${searchType}
from tbl_approval where status = 1 and lower(${searchType}) like '%'||lower(#{searchWord})||'%'
order by ${searchType} asc
order by seq asc
