mapper.select:
select employee_no, fk_department_no, fk_team_no, name_kr, name_en, passwd, jointype, manager_no, hire_date, salary, commission_pct, mobile, postcode, address, detail_address, extra_address, email, gender, profile_systemfilename, profile_orginfilename, academic_ability, major, militaryservice, bank, accountnumber, role, position, authority,birthday
from tbl_employee
where delete_status = 1 and employee_no = #{userid}

select employee_no, fk_department_no, department_name, fk_team_no, team_name, name_kr, name_en, passwd, jointype, to_char(hire_date,'yyyy. mm. dd') as hire_date, salary, commission_pct, mobile, postcode, address, detail_address, extra_address, email, gender, profile_systemfilename, profile_orginfilename, academic_ability, major, militaryservice, bank, accountnumber, delete_status, role, position, authority, to_char(birthday,'yyyy-mm-dd') as birthday, dayoff_cnt, employmenttype
from v_employee
where employee_no = #{empno}

select profile_systemfilename
from tbl_employee
where employee_no = #{employee_no}

select count(*) from ( select row_number() over(order by employee_no desc) as rno, employee_no, fk_department_no, department_name, fk_team_no, team_name, name_kr, jointype, mobile, delete_status, role, position, authority from v_employee where 1 = 1
and authority != 1
and ${dropCondition} = #{dropVal}
and ${dropCondition} is null
and lower(${searchCondition}) like '%'||lower(#{searchVal})||'%'
)

select employee_no, fk_department_no, department_name, fk_team_no, team_name, name_kr, jointype, email, mobile, delete_status, role, position, authority, employmenttype from ( select row_number() over(order by employee_no desc) as rno, employee_no, fk_department_no, department_name, fk_team_no, team_name, name_kr, jointype, email, mobile, delete_status, role, position, authority,employmenttype from v_employee where 1 = 1
and authority != 1
and ${dropCondition} = #{dropVal}
and ${dropCondition} is null
and lower(${searchCondition}) like '%'||lower(#{searchVal})||'%'
) where rno between #{startRno} and #{endRno}

select team_no, team_name
from tbl_team
where fk_department_no = #{deptno}
